    /**
     *  获取 一个数组中连续(步长为 1 )有序大于等于thresholdNum数量的 数字的 下标区间
     * @param integerList   目标集合
     * @param thresholdNum  阈值(最低 连续有序 的 数量)
     * @return  一个数组中连续有序大于等于thresholdNum数量的 数字的 下标区间
     */
    private List<Integer> getSequenceIndexSection(List<Integer> integerList, Integer thresholdNum) {
        List<Integer> sequenceIndex = new ArrayList<>();
        //连续数量
        int ascendCount = 0;
        int descendCount = 0;
        //true : 升序; flase : 降序
        boolean sortFlag = false;
        Integer startIndex = -1;
        for (int i = 0; i < integerList.size() - 1; i++) {
            Integer nowInteger = integerList.get(i);
            Integer nextInteger = integerList.get(i + 1);
            //连续的 判断标准 : 步长为 1
            //升序
            if (nowInteger - nextInteger == 1) {
                if (sortFlag) {
                    if (startIndex == -1) {
                        startIndex = i;
                    }
                    ascendCount ++;
                } else {
                    //状态(趋势) 切换
                    if (descendCount >= thresholdNum) {
                        sequenceIndex.add(startIndex);
                        sequenceIndex.add(i);
                    }
                    startIndex = i;
                    ascendCount = 1;
                    descendCount = 0;
                    sortFlag = true;
                }
            }
            //降序
            if (nowInteger - nextInteger == -1) {
                if (!sortFlag) {
                    if (startIndex == -1) {
                        startIndex = i;
                    }
                    descendCount ++;
                } else {
                    //状态(趋势) 切换
                    if (ascendCount >= thresholdNum) {
                        sequenceIndex.add(startIndex);
                        sequenceIndex.add(i);
                    }
                    startIndex = i;
                    descendCount = 1;
                    ascendCount = 0;
                    sortFlag = false;
                }
            }
            //步长 不是 1
            if (Math.abs(nowInteger - nextInteger) != 1) {
                if (descendCount >= thresholdNum || ascendCount >= thresholdNum) {
                    sequenceIndex.add(startIndex);
                    sequenceIndex.add(i);
                }
                startIndex = -1;
                ascendCount = 0;
                descendCount = 0;
            }

        }
        return sequenceIndex;
    }

    /**
     * 获取 一个数组中无序的 数字的 下标区间
     * @param integerList
     * @return  无序的 数字的 下标区间 的 集合
     */
    private List<Integer> getOutOfOrderIndex(List<Integer> integerList) {
        List<Integer> outOfOrderIndex = new ArrayList<>();
        for (int i = 0; i < integerList.size() - 1; i++) {
            Integer integer = integerList.get(i);
            //判断是否 乱序
            if (integer > integerList.get(i + 1)) {
                int maxNum = integer;
                for (int j = i; j < maxNum; j ++) {
                    if (maxNum < integerList.get(j)) {
                        maxNum = integerList.get(j);
                    }
                }
                outOfOrderIndex.add(i);
                outOfOrderIndex.add(maxNum - 1);
                i = maxNum;
            }
        }
        return outOfOrderIndex;
    }

